#!/usr/bin/env python3


set_seq = ""

#DNA_uppercase = DNA.upper() #move the seq to new variable with same case characters
#print(DNA_uppercase)

DNA_length = len(set_seq)

num_a = set_seq.count('A')
num_t = set_seq.count('T')
num_c = set_seq.count('C')
num_g = set_seq.count('G')

string = "A:{} T:{} C:{} G:{}"

print(string.format(num_a, num_t, num_c, num_g))

nt_count = {}

for nt in set_seq:

	if nt in nt_count:
	  previous_count = nt_count[nt]
	  new_count = previous_count + 1
	  nt_count[nt] = new_count
	else:
	  nt_count[nt] = 1;

print(nt_count)

GC_comp = 0
GT_comp = (nt_count['G'] + nt_count['C'])/DNA_length

print("GC composition:", GT_comp)





