
#!/usr/bin/env python3


DNA = ''

DNA_uppercase = DNA.upper() #move the seq to new variable with same case characters
#print(DNA_uppercase)

DNA_length = len(DNA_uppercase)

num_a = DNA_uppercase.count('A')
num_t = DNA_uppercase.count('T')
num_c = DNA_uppercase.count('C')
num_g = DNA_uppercase.count('G')

AT_content = (num_a + num_t)/DNA_length
GC_content = (num_c + num_g)/DNA_length

#RNA_uppercase = DNA_uppercase.replace('T','U')

print('The DNA sequence: ', DNA_uppercase, '\n', 'AT content is', AT_content, '\n', 'GC content is', GC_content)
